import React, { Component } from 'react';
import { Router, Route, withRouter } from 'react-router-dom';
import { Blue, Green, Purple, Red } from './colors';
import classes from './App.module.css';


class App extends Component {
    blueClickHandler () {
        this.props.history.push('/blue')
    }

    greenClickHandler () {
        this.props.history.push('/green')
    }
    purpleClickHandler () {
        this.props.history.push('/purple')
    }
    redClickHandler () {
        this.props.history.push('/red')
    }

    render() {
        return (
            <div className={classes.page}>
                    <h2>Choose a Color</h2>
                <div className={classes.btnHolder}>
                    <button onClick={() => this.blueClickHandler()}>Blue</button>
                    <button onClick={() => this.greenClickHandler()}>Green</button>
                    <button onClick={() => this.purpleClickHandler()}>Purple</button>
                    <button onClick={() => this.redClickHandler()}>Red</button>
                </div>
            </div>
        );
    } 
}

export default App;
