import React from 'react';
import './Game.css';

class Square extends React.Component {
    render() {
        return (
            <button className="square" onClick={this.props.clicked}>
                {this.props.value}
            </button>
        );
    }
}
//////////////////////////////////////
class Board extends React.Component {
    renderSquare(i) {
        return (
            <Square
                value={this.props.squares[i]}
                clicked={() => this.props.clicked(i)}
            />
        );
    }

    render() {
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}
/////////////////////////////////////
class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentPlayerX: true,
            squares: Array(9).fill(null),
            winner: null
        }
    }

    handleClick(ind) {
        if (this.state.squares.every((el) => el !== null)) {
            return;
        }

        console.log(this.state.squares);

        this.setState((prevState, props) => ({
            squares: prevState.squares.map((el, index) => {
                  if (index === ind) {
                      if (this.state.squares[ind] !== null) {
                          return el;
                      } else {
                        //The setState for currentPlayerX used to be here, but the problem still existed.
                          if(this.state.currentPlayerX){
                              return 'X';
                          } else {
                              return 'O';
                          } 
                      }
                  } else {
                      return el;
                  }
              })
          }));

        console.log(this.state.squares);

        this.setState((prevState, props) => ({
            currentPlayerX: !prevState.currentPlayerX
        }));

        let xCount = 0;
        let oCount = 0;

        for (let i = 0; i < this.state.squares.length; i++) {
            if (this.state.squares[i] === 'X') {
                xCount++;
            }
            if (this.state.squares[i] === 'O') {
                oCount++;
            }
        }

        const wins = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];
        if (xCount >= 3) {
            const currentXValueIndexes = this.state.squares
                .map((el, i) => {
                    return el === 'X' ? i : 'O';
                })
                .filter((el) => {
                    return el !== 'O';
                });
            for (let i = 0; i < wins.length; i++) {
                if (
                    currentXValueIndexes.includes(wins[i][0]) &&
                    currentXValueIndexes.includes(wins[i][1]) &&
                    currentXValueIndexes.includes(wins[i][2])
                ) {
                    this.setState({ winner: 'X' });
                    console.log('the winner is X');
                }
            }
        }
        if (oCount >= 3) {
            const currentOValueIndexes = this.state.squares
                .map((el, i) => {
                    return el === 'O' ? i : 'X';
                })
                .filter((el) => {
                    return el !== 'X';
                });
            for (let i = 0; i < wins.length; i++) {
                if (
                    currentOValueIndexes.includes(wins[i][0]) &&
                    currentOValueIndexes.includes(wins[i][1]) &&
                    currentOValueIndexes.includes(wins[i][2])
                ) {
                    this.setState({ winner: 'O' });
                    console.log('the winner is O');
                }
            }
        }
    }

    render() {
        let winStatement = <p>The winner is: {this.state.winner}</p>;
        return (
            <div className="game">
                <div className="game-board">
                    {winStatement}
                    <Board
                        clicked={(ix) => this.handleClick(ix)}
                        squares={this.state.squares}
                    />
                </div>
                <div className="game-info">
                    <div>
                        The current player is{' '}
                        {this.state.currentPlayerX ? 'X' : 'O'}
                    </div>
                    <ol>{/* TODO */}</ol>
                </div>
            </div>
        );
    }
}

export default Game;
