import React from 'react';
import './App.css';
import SevenDay from './Seven Day Forecast/sevenDay';
import SideMenu from './Side Menu/SideMenu';
import { BrowserRouter, Route } from 'react-router-dom';
import CityBox from './City Box/cityBox';
import Hourly from './Hourly Forecast/HourlyForecast';
import CurrentWeather from './Current Weather/CurrentWeather';

function App() {
    return (
        <BrowserRouter>
            <header
                style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    color: 'white'
                }}
            >Welcome to my Demo Weather Page</header>
            <div style={{
                display: 'flex',
                height: '200px',
                marginTop: '20px'
            }}>
                <SideMenu />
                <CityBox city={'Houston'} />
            </div>
            <Route path="/sevenDay" component={SevenDay} />
            <Route path="/hourly" component={Hourly} />
            <Route path="/current" component={CurrentWeather} />
        </BrowserRouter>
    );
}

export default App;
