import React, { Component } from 'react';
import Banner from '../Banner/banner';
import { connect } from 'react-redux';

class currentWeather extends Component {
    render() {
        const currentWeatherBlock = null;

        if (this.currentData !== null) {
            currentWeatherBlock = (
                <div>
                    <img src={this.currentImg} alt={'Current Weather Icon'} />
                    <p>Current Temperature:{' '}
                        {Math.round(this.currentData.temp.toFixed(0))}{' '}
                    </p>
                    <p>Feels Like: {this.currentData.feels_like}</p>
                    <p>Description: {this.currentData.weather.description}</p>
                    <p>
                        Wind:{' '}
                        {Math.round(this.currentData.wind_speed.toFixed(0))} mph
                    </p>
                </div>
            );
        }

        return (
            <div>
                <Banner bannerTitle="Current Weather" />
                <div>{currentWeatherBlock}</div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        currentImg: state.currentImage,
        currentData: state.currentData,
    };
};

export default connect(mapStateToProps)(currentWeather);
