import React, { Component } from 'react';
import mostlySunnyImg from '../img/partlysunny.png';
import rainyImg from '../img/rainy.png';
import snowyImg from '../img/snowy.png';
import stormyImg from '../img/stormy.png';
import sunnyImg from '../img/sunny.png';
import WeatherCard from '../Weather Card/weatherCard';
import classes from './sevenDay.module.css';

const images = [mostlySunnyImg, rainyImg, snowyImg, stormyImg, sunnyImg];

class sevenDay extends Component {
    constructor(props) {
        super(props);
        this.state = {
            images: images,
            highTemps: ['72', '60', '35', '76', '91'],
            lowTemps: ['60', '48', '21', '63', '79'],
            weatherDescriptions: [],
        };
    }

    render() {
        return (
            <div className={classes.weatherRow}>
                {Array(7).fill(null).map((el, index) => {
                    return (
                        <WeatherCard
                            img={this.state.images[index]}
                            highTemp={`High: ${this.state.highTemps[index]}째`}
                            lowTemp={`Low 60째`}
                            weatherDescription={'Mostly Sunny'}
                            date={new Date().toLocaleDateString('en-US', {
                                weekday: 'long',
                                year: 'numeric',
                                month: 'long',
                                day: 'numeric',
                            })}
                        />
                    );
                })}
            </div>
        );
    }
}

export default sevenDay;
