import React, { Component } from 'react';
import classes from './cityBox.module.css';
import axios from 'axios';

class cityBox extends Component {
    state = {
        searchString: null,
        currentCity: null,
        currentCityTemp: null
    };

    inputChangeHandler = (event) => {
        this.setState({ searchString: event.target.value });
    };

    newCityChangeHandler(event) {
        axios
            .get(
                `https://cors-anywhere.herokuapp.com/https://api.openweathermap.org/data/2.5/weather?q=${event.target.value}&appid=da9156d2392f013a7e000b4e71847f75`
            )
            .then(response => {
                this.setState({
                    currentCity: response.data.name,
                    currentCityTemp: (response.data.main.temp - 273.15) * 9 / 5 + 32
                });
                console.log(response);
            })
            .catch(err => {
                alert("Please enter a valid city");
            });
    }

    render() {
        return (
            <div className={classes.header}>
                <div className={classes.city}>
                    <h2>Current City: {this.state.currentCity}</h2>
                </div>
                <div className={classes.searchBox}>
                    <p>Search (City)</p>
                    <input
                        onChange={(e) => this.inputChangeHandler(e)}
                        onKeyDown={(ev) => {
                            if (ev.key === 'Enter') {
                                this.newCityChangeHandler(ev);
                            }
                        }}
                    ></input>
                </div>
                <p>{this.state.currentCityTemp ? `Current Temperature: ${this.state.currentCityTemp}°` : "(Current Temperature Will Display Here)"}</p>
            </div>
        );
    }
}

export default cityBox;
