import React from 'react';
import mostlySunnyImg from '../img/mostlysunny.jfif';
import WeatherCard from '../Weather Card/weatherCard';
import classes from './weatherCardRow.module.css';

const weatherCards = Array(5)
    .fill(null)
    .map((el, index) => {
        return (
            <WeatherCard
                img={mostlySunnyImg}
                temperature={'72/60'}
                weatherDescription={'Mostly Sunny'}
                date={new Date().toLocaleDateString('en-US', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                })}
            />
        );
    });

const weatherCardRow = (props) => {
    return <div className={classes.weatherRow}>{weatherCards}</div>;
};

export default weatherCardRow;
