import React, { Component } from 'react';
import mostlySunnyImg from '../img/mostlysunny.jfif';
import sunnyImg from '../img/sunny.png';
import snowyImg from '../img/snowy.png';
import WeatherCard from '../Weather Card/weatherCard';
import classes from './weatherCardRow.module.css';

const weatherCards = Array(5)
    .fill(null)
    .map((el, index) => {
        return (
            <WeatherCard
                img={mostlySunnyImg}
                highTemp={`High: 72­°`}
                lowTemp={`Low 60°`}
                weatherDescription={'Mostly Sunny'}
                date={new Date().toLocaleDateString('en-US', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                })}
            />
        );
    });

class weatherCardRow extends Component {
    constructor (props) {
        super(props);
        this.state = {
            images: [],
            highTemps: [],
            lowTemps: [],
            weatherDescriptions: []
        }
    }

    render () {
        return (
            <div className={classes.weatherRow}>{weatherCards}</div>
        )
    } 
};

export default weatherCardRow;
