import React, { Component } from 'react';
import mostlySunnyImg from '../img/mostlysunny.jfif';
import rainyImg from '../img/snowy.png';
import snowyImg from '../img/snowy.png';
import stormyImg from '../img/snowy.png';
import sunnyImg from '../img/sunny.png';
import WeatherCard from '../Weather Card/weatherCard';
import classes from './weatherCardRow.module.css';

class weatherCardRow extends Component {
    constructor(props) {
        super(props);
        this.state = {
            images: [mostlySunnyImg, rainyImg, snowyImg, stormyImg, sunnyImg],
            highTemps: [],
            lowTemps: [],
            weatherDescriptions: [],
        };
    }

    render() {
        return (
            <div className={classes.weatherRow}>
                {Array(5).fill(null).map((el, index) => {
                    return (
                        <WeatherCard
                            img={mostlySunnyImg}
                            highTemp={`High: 72­°`}
                            lowTemp={`Low 60°`}
                            weatherDescription={'Mostly Sunny'}
                            date={new Date().toLocaleDateString('en-US', {
                                weekday: 'long',
                                year: 'numeric',
                                month: 'long',
                                day: 'numeric',
                            })}
                        />
                    );
                })}
            </div>
        );
    }
}

export default weatherCardRow;
