import mostlySunnyImg from '../../img/partlysunny.png';
import rainyImg from '../../img/rainy.png';
import snowyImg from '../../img/snowy.png';
import stormyImg from '../../img/stormy.png';
import sunnyImg from '../../img/sunny.png';

const images = [mostlySunnyImg, rainyImg, snowyImg, stormyImg, sunnyImg];

const initialState = {
    images: images,
    highTemps: ['72', '60', '35', '76', ''],
    lowTemps: ['60', '48', '21', '63', '79'],
    weatherDescs: ['Mostly Sunny', 'Rainy', 'Snowy', 'Stormy', 'Sunny']
};

const reducer = (state = initialState, action) => {
  return state;
};

export default reducer;
