import React, { Component } from 'react';
import classes from './cityBox.module.css';
import axios from 'axios';

class cityBox extends Component {
    state = {
        searchString: null,
    };

    inputChangeHandler = (event) => {
        this.setState({ searchString: event.target.value });
    };

    newCityChangeHandler(event) {
        axios
            .get(
                `https://cors-anywhere.herokuapp.com/https://samples.openweathermap.org/data/2.5/weather?q=London,uk&appid=439d4b804bc8187953eb36d2a8c26a02`
            )
            .then(response => {
                console.log(response);
            })
            .catch(err => {
                console.log(err.message);
            });
    }

    render() {
        return (
            <div className={classes.header}>
                <div className={classes.city}>
                    <h2>Current City: Houston</h2>
                </div>
                <div className={classes.searchBox}>
                    <p>Search (City)</p>
                    <input
                        onChange={(e) => this.inputChangeHandler(e)}
                        onKeyDown={(ev) => {
                            if (ev.key === 'Enter') {
                                this.newCityChangeHandler(ev);
                            }
                        }}
                    ></input>
                </div>
            </div>
        );
    }
}

export default cityBox;
