import React, { Component } from 'react';
import Banner from '../Banner/banner';

class hourly extends Component {
    render() {
        return (
            <div>
                <Banner bannerTitle="Hourly Forecast" />
                <div className={classes.weatherRow}>
                    {Array(7)
                        .fill(null)
                        .map((el, index) => {
                            return (
                                <WeatherCard
                                    img={this.state.images[index]}
                                    highTemp={`High: ${this.state.highTemps[index]}째`}
                                    lowTemp={`Low 60째`}
                                    weatherDescription={'Mostly Sunny'}
                                    date={new Date().toLocaleDateString(
                                        'en-US',
                                        {
                                            weekday: 'long',
                                            year: 'numeric',
                                            month: 'long',
                                            day: 'numeric',
                                        }
                                    )}
                                />
                            );
                        })}
                </div>
            </div>
        );
    }
}

export default hourly;
