import React, { Component } from 'react';
import Banner from '../Banner/banner';
import WeatherCard from '../Weather Card/weatherCard';
import mostlySunnyImg from '../img/partlysunny.png';
import rainyImg from '../img/rainy.png';
import snowyImg from '../img/snowy.png';
import stormyImg from '../img/stormy.png';
import sunnyImg from '../img/sunny.png';
import classes from './HourlyForecast.module.css';

const images = [mostlySunnyImg, rainyImg, snowyImg, stormyImg, sunnyImg];

class hourly extends Component {
    constructor(props) {
        super(props);
        this.state = {
            images: images,
            highTemps: ['72', '60', '35', '76', '91'],
            lowTemps: ['60', '48', '21', '63', '79'],
            weatherDescriptions: [],
        };
    }

    render() {
        return (
            <div>
                <Banner bannerTitle="Hourly Forecast" />
                <div className={classes.hourlyRow}>
                    {Array(7)
                        .fill(null)
                        .map((el, index) => {
                            return (
                                <WeatherCard
                                    img={this.state.images[index]}
                                    highTemp={`High: ${this.state.highTemps[index]}째`}
                                    lowTemp={`Low 60째`}
                                    weatherDescription={'Mostly Sunny'}
                                    date={new Date().toLocaleDateString(
                                        'en-US',
                                        {
                                            weekday: 'long',
                                            year: 'numeric',
                                            month: 'long',
                                            day: 'numeric',
                                        }
                                    )}
                                />
                            );
                        })}
                </div>
            </div>
        );
    }
}

export default hourly;
