import mostlySunnyImg from '../../img/partlysunny.png';
import rainyImg from '../../img/rainy.png';
import snowyImg from '../../img/snowy.png';
import stormyImg from '../../img/stormy.png';
import sunnyImg from '../../img/sunny.png';
import cloudyImg from '../../img/cloudy.png';
import questionMarkImg from '../../img/questionMark.png';

const images = [questionMarkImg, questionMarkImg, questionMarkImg, questionMarkImg, questionMarkImg, questionMarkImg, questionMarkImg];

const imagesObj = {
  'Clear': sunnyImg,
  'Clouds': cloudyImg
}

const initialState = {
    images: images,
    highTemps: ['', '', '', '', '', '', ''],
    lowTemps: ['', '', '', '', '', '', ''],
    weatherDescs: ['', '', '', '', '', '', '']
};

const reducer = (state = initialState, action) => {
  if(action.type === 'FETCH_WEATHER'){
    console.log(action);
    return {
      ...state,
      highTemps: action.resData.daily.map(el => {
        return Math.round(((el.temp.max - 273.15) * 9) / 5 + 32);
      }),
      lowTemps: action.resData.daily.map(el => {
        return Math.round(((el.temp.min - 273.15) * 9) / 5 + 32);
      }),
      images: action.resData.daily.map(el => {
        if(imagesObj[el.weather[0].main]){
          return imagesObj[el.weather[0].main];
        } else {
          return sunnyImg;
        }
      })
    }
  }
  return state;
};

export default reducer;
