import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import YouTube from 'react-youtube';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoId: null,
            resultVideoTitles: null,
            resultVideoIds: null,
            searchString: null,
        };
    }

    searchHandler() {
        axios
            .get(
                `https://www.googleapis.com/youtube/v3/search?part=snippet&key=AIzaSyDeMhcfA8lM8PGnMAkPl2n76hog-lAzgxc&type=video&q=${this.state.searchString}`
            )
            .then((response) => {
                this.setState({
                    resultVideoTitles: response.data.items.map((el) => {
                        return el.snippet.title;
                    }),
                    resultVideoIds: response.data.items.map((el) => {
                        return el.id.videoId;
                    }),
                });
            })
            .catch((err) => alert(err));
    }

    render() {
        return (
            <div>
                <header>
                    <p>YouTube Demo</p>
                    <input
                        onChange={(event) => {
                            this.setState({
                                searchString: event.target.value,
                            });
                        }}
                        onKeyDown={(event) => {
                            console.log(event.key);
                        }}
                    ></input>
                    <button onClick={() => this.searchHandler()}>Search</button>
                </header>
                <div className="App">
                    <YouTube
                        videoId={this.state.videoId ? this.state.videoId : null}
                        opts={{
                            playerVars: {
                                autoplay: 1,
                            },
                        }}
                    />
                    <div
                        style={{
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-evenly',
                            width: '35%',
                            height: '300px',
                        }}
                    >
                        {this.state.resultVideoTitles ? (
                            this.state.resultVideoTitles.map((title, index) => {
                                return (
                                    <button
                                        style={{
                                            backgroundColor: 'black',
                                            width: '100%',
                                        }}
                                        onClick={(event) =>
                                            this.setState({
                                                videoId: this.state
                                                    .resultVideoIds[index],
                                            })
                                        }
                                    >
                                        {title}
                                    </button>
                                );
                            })
                        ) : (
                            <p></p>
                        )}
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
