import React, { Component } from 'react';
import './App.css';
import YouTube from './Youtube';
import Results from './Results';
import axios from 'axios';

class App extends Component {
    constructor (props) {
        super(props);
        this.state = {
            vidId: null
        }
    }

    searchHandler() {
        axios
            .get(
                'https://www.googleapis.com/youtube/v3/videos?id=p5Rlgu4L9J4&key=AIzaSyDeMhcfA8lM8PGnMAkPl2n76hog-lAzgxc'
            )
            .then((response) => {
                console.log(response);
                this.setState({vidId: response.data.items[0].id});
            })
            .catch((err) => console.log(err));
    }

    render () {
        return (
            <div>
                <header>
                    <p>YouTube Demo</p>
                    <input></input>
                    <button onClick={() => this.searchHandler()}>Search</button>
                </header>
                <div className="App">
                    <YouTube />
                    <Results />
                </div>
            </div>
        );
    }
}

export default App;
