import React, { Component } from 'react';
import YouTube from 'react-youtube';
import axios from 'axios';

class YouTubeComp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            vidId: null,

        };
    }

    searchHandler() {
        axios
            .get(
                'https://www.googleapis.com/youtube/v3/videos?id=7lCDEYXw3mM&key=AIzaSyDeMhcfA8lM8PGnMAkPl2n76hog-lAzgxc'
            )
            .then((response) => {
                console.log(response);
                this.setState({vidId: response.data.items[0].id});
            })
            .catch((err) => console.log(err));
    }

    render() {
        const options = {
            height: '390',
            width: '640',
            playerVars: {
                autoplay: 0,
            },
        };

        console.log(this.state.vidId);

        return (
            <div>
                <header>
                    <p>YouTube Demo</p>
                    <input></input>
                    <button onClick={() => this.searchHandler()}>Search</button>
                </header>
                <YouTube videoId={this.state.vidId} opts={options} />
            </div>
        );
    }
}

export default YouTubeComp;
