import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import YouTube from 'react-youtube';

class App extends Component {
    constructor (props) {
        super(props);
        this.state = {
            videoId: null,
            resultVideoTitles: null,
            searchString: null
        }
    }

    searchHandler() {
        axios
            .get(
                `https://www.googleapis.com/youtube/v3/search?part=snippet&key=AIzaSyDeMhcfA8lM8PGnMAkPl2n76hog-lAzgxc&type=video&q=${this.state.searchString}`
            )
            .then((response) => {
                console.log(response);
                this.setState({
                    resultVideoTitles: response.data.items.map(el => {
                        return el.id.videoId
                    })
                })
            })
            .catch((err) => console.log(err));
    }

    render () {
        return (
            <div>
                <header>
                    <p>YouTube Demo</p>
                    <input onChange={(event) => {
                        console.log(event.target.value);
                        this.setState({
                            searchString: event.target.value
                        })
                    }}></input>
                    <button onClick={() => this.searchHandler()}>Search</button>
                </header>
                <div className="App">
                    <YouTube />
                    <div>
                        {this.state.resultVideoTitles ? null : <p>Results go here</p>}
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
