import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import YouTube from 'react-youtube';

class App extends Component {
    constructor (props) {
        super(props);
        this.state = {
            videoId: null,
            resultVideoTitles: null,
            resultVideoIds: null,
            searchString: null
        }
    }

    searchHandler() {
        axios
            .get(
                `https://www.googleapis.com/youtube/v3/search?part=snippet&key=AIzaSyDeMhcfA8lM8PGnMAkPl2n76hog-lAzgxc&type=video&q=${this.state.searchString}`
            )
            .then((response) => {
                console.log(response);
                this.setState({
                    resultVideoTitles: response.data.items.map(el => {
                        return el.snippet.title;
                    }),
                    resultVideoIds: response.data.items.map(el => {
                        return el.id.videoId;
                    })
                })
            })
            .catch((err) => console.log(err));
    }

    render () {
        return (
            <div>
                <header>
                    <p>YouTube Demo</p>
                    <input onChange={(event) => {
                        console.log(event.target.value);
                        this.setState({
                            searchString: event.target.value
                        })
                    }}></input>
                    <button onClick={() => this.searchHandler()}>Search</button>
                </header>
                <div className="App">
                    <YouTube 
                        videoId={this.state.videoId ? this.state.videoId : null}
                        opts={{
                            playerVars: {
                                autoplay: 1
                            }
                        }}
                    />
                    <div>
                        {this.state.resultVideoTitles ? this.state.resultVideoTitles.map((title, index) => {
                            return <button
                                    onClick={(event) => this.setState({videoId: this.state.resultVideoIds[index]})}
                                >{title}</button>
                        }) : <p>Results go here</p>}
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
